php <?php
// Retrieve shop name from POST request $shopName = $_POST['shopName'];  // Validate shop name if (!preg_match('/^[a-zA-Z0-9\s]+$/', $shopName)) {   echo 'Invalid shop name';   return; }  // Update shop name in the MySQL table // Replace 'your_db_name', 'your_db_username', 'your_db_password', and 'your_table_name' with your actual database credentials $servername = 'localhost'; $dbname = 'your_db_name'; $username = 'your_db_username'; $password = 'your_db_password'; $tableName = 'your_table_name';  try {   $conn = new PDO("mysql:host=$servername;dbname=$dbname", $username, $password);   $conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);    $stmt = $conn->prepare("UPDATE $tableName SET shop_name = :shopName");   $stmt->bindParam(':shopName', $shopName);   $stmt->execute();    echo $shopName; // Return updated shop name } catch(PDOException $e) {   echo 'An error occurred while updating the shop name'; }  $conn = null;
?> 
